import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function SignInPage3({ formData, submitForm, setUserId }) {
    const navigate = useNavigate();
    const [nicknameError, setNicknameError] = useState("");
    const [hasChecked, setHasChecked] = useState(false);
    const [isInputChanged, setIsInputChanged] = useState(false);

    const handleNicknameChange = async (e) => {
        const value = e.target.value;

        // Validate nickname on change
        await validateNickname(value);
        setHasChecked(true);
        if (!isInputChanged) setIsInputChanged(true);
    };

    const validateNickname = async (nickname) => {
        if (!nickname.trim()) {
            setNicknameError("Nickname is required.");
            return false;
        }

        try {
            const response = await fetch(`/api/check-nickname?nickname=${nickname}`);
            const { isAvailable } = await response.json();

            if (!isAvailable) {
                setNicknameError("Sorry, that username is already taken!");
                return false;
            }

            setNicknameError(""); // No errors
            return true;
        } catch (error) {
            setNicknameError("Error checking nickname availability. Please try again.");
            return false;
        }
    };

    return (
        <div>
            <div className="signInBox">
                <div className="leftBox">
                    <img src="img/fisrtpage.svg" alt="animation" />
                </div>
                <div className="rightBox first_right">
                    <div className="bigEmo">ðŸ™Œ</div>
                    <div className="top signIn_top">
                        <div>Sign in Here!</div>
                        <div>Welcome back to ARTBOOK</div>
                    </div>
                    <fieldset>
                        <div className="signIn_bottom">
                            <div className={`name_input ${nicknameError ? "error_state" : ""}`}>
                                <input type="text" value={nickname} placeholder="Username" onChange={handleNicknameChange} />
                                <img src="img/name.svg" alt="name" className="nameImg"  />
                                {nicknameError && <div className="error">{nicknameError}</div>}
                            {hasChecked && ( 
                                <img
                                    src={nicknameError ? "img/error.svg" : "img/success.svg"}
                                    alt={nicknameError ? "error" : "success"}
                                    className="errorImg"
                                />
                                )}
                            </div>  
                            <button className="signInBtn">Sign in</button>
                        </div>
                    </fieldset>
                    <div className="goToSignUp">
                        <div>Don't have an account?</div>
                        <div onClick={() => navigate("/sign-up")}>Sign up</div>
                    </div>
                </div>
            </div>
        </div>
      );
}

export default SignInPage3;
