import React from 'react';
import { useNavigate } from 'react-router-dom';

function SignInPage2({ formData, setFormData, nextStep }) {
    const navigate = useNavigate();

    const handleNicknameChange = (e) => {
    setFormData({ ...formData, nickname: e.target.value });
    };

    // The purpose of frontetnd validation is to give feedback to the user.
    // this is different from backend validation (which is more about enforcing the rules.)
    const validateItem = (data) => {
        const errors = [];  
        if (data.description.length < 10) errors.push('Description is too short.');  
        noticeArea.style.display = errors.length ? 'block' : 'none';
        noticeArea.textContent = errors.join(' ');
        return errors.length === 0;
    };


  return (
    <div>
        <div className="signInBox">
            <div className="leftBox">
                <img src="img/fisrtpage.svg" alt="animation" />
            </div>
            <div className="rightBox">
                <form className="signInForm">
                    <div>
                        <div className="progressBar">
                            <div className="progress_two"></div>
                        </div>
                        <div className="body_text">Sign up to ARTBOOK!</div>
                    </div>

                    <fieldset className="nickname">
                        <div className="showEmoji">
                            <div>{formData.emojis}</div>
                        </div>

                        <div>What's your name?</div>
                        <div className="name_input">
                            <input type="text" value={formData.nickname} placeholder="Name" onChange={handleNicknameChange} />
                            <img src="img/name.svg" alt="name" />
                        </div>  
                    </fieldset>

                    <fieldset className="btns">
                        <button className="skipBtn form_btn" onClick={() => navigate("/")}>Cancel</button>
                        <div>Already have an account? <div className="signIn" onClick={() => navigate("/sign-in")}>Sign in</div></div>
                        <button className="nextBtn form_btn" onClick={nextStep}>Next <img src="img/nextArrow.svg" alt="right arrow" /></button>
                    </fieldset>
                </form>
            </div>
        </div>
    </div>
  );
}

export default SignInPage2;
