import React from 'react';
import { useNavigate } from 'react-router-dom';

function SignInPage2({ formData, setFormData, nextStep }) {
    const navigate = useNavigate();

    const handleNicknameChange = (e) => {
    setFormData({ ...formData, nickname: e.target.value });
    };

  return (
    <div>
        <nav>
            {/* It redirects to the home page when user click the logo */}
            {/* https://stackoverflow.com/questions/29552601/how-to-set-the-defaultroute-to-another-route-in-react-router */}
            <div><img src="img/logo.svg" alt="ArtBook logo" onClick={() => navigate("/")} /></div>
            <div className="header"></div>
        </nav>
        <div className="signInBox">
            <div className="leftBox"></div>
            <div className="rightBox">
                <form className="signInForm">
                    <div>
                        <div className="progressBar">
                            <div className="progress_one"></div>
                        </div>
                        <div className="body_text">Sign up to ARTBOOK!</div>
                    </div>

                    <fieldset className="nickname">
                        <div className="showEmoji">
                            <div>{formData.emojis}</div>
                        </div>

                        <div>What's your name?</div>
                        <div className="name_input">
                            <input type="text" value={formData.nickname} placeholder="Name" onChange={handleNicknameChange} />
                            <img src="img/name.svg" alt="name" />
                        </div>  
                    </fieldset>

                    <fieldset className="btns">
                        <button className="skipBtn form_btn" onClick={() => navigate("/list")}>Skip</button>
                        <button className="nextBtn form_btn" onClick={nextStep}>Next <img src="img/nextArrow.svg" alt="right arrow" /></button>
                    </fieldset>
                </form>
            </div>
        </div>
    </div>
  );
}

export default SignInPage2;
